sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	datewhatneeded: function() {
		var nowtime = new GlideDateTime();
	var whentime = new GlideDateTime(current.u_when_needed);



	if (whentime.before(nowtime) ) {
		gs.addErrorMessage("Past date cannot be selected, your request is not saved");
		current.setAbortAction(true);
		current.u_when_needed = "";


	var nowtime1 = nowtime.getLocalDate();
	var whentime1 = whentime.getLocalDate();

		if(whentime1.equals(nowtime1)) {
			gs.addErrorMessage("Same day requests are not allowed");
			current.setAbortAction(true);
		current.u_when_needed = "";
		}

	}


	},

    type: 'NeedItUtils'
};
